import axios from 'axios';
axios.defaults.baseURL = "http://localhost:5282";
axios.defaults.headers.common['Authorization'] = AUTH_TOKEN;
axios.defaults.headers.post['Content-Type'] = 'application/x-www-form-urlencoded';

const apiUrl = "http://localhost:5282"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result=await axios.post(`${apiUrl}/items`, {name})
    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result=await axios.put(`${apiUrl}/items/${id}`, {isComplete})
    console.log(result.data)
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
   const result= await axios.delete(`${apiUrl}/items/${id}`)
    return result.data;
  },
};
